import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();

declare module 'http' {
  interface IncomingMessage {
    rawBody: unknown
  }
}
app.use(express.json({
  verify: (req, _res, buf) => {
    req.rawBody = buf;
  }
}));
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { cloneService } from "./services/clone";
import { fileManager } from "./services/fileManager";
import { insertProjectSchema } from "@shared/schema";
import { WebSocketServer } from "ws";
import archiver from "archiver";
import path from "path";

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // WebSocket for progress updates
  const wss = new WebSocketServer({ server: httpServer, path: "/ws" });

  wss.on("connection", (ws) => {
    console.log("WebSocket client connected");

    ws.on("close", () => {
      console.log("WebSocket client disconnected");
    });
  });

  // Broadcast progress to all connected clients
  function broadcastProgress(projectId: string, progress: any) {
    wss.clients.forEach((client) => {
      if (client.readyState === 1) {
        client.send(JSON.stringify({ projectId, ...progress }));
      }
    });
  }

  // Get all projects
  app.get("/api/projects", async (_req, res) => {
    try {
      const projects = await storage.getAllProjects();
      res.json(projects);
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Failed to fetch projects",
      });
    }
  });

  // Create new project and start cloning
  app.post("/api/projects", async (req, res) => {
    try {
      const data = insertProjectSchema.parse(req.body);

      const project = await storage.createProject(data);

      // Start cloning in background
      cloneService
        .cloneWebsite(
          project.id,
          data.url,
          (progress, step, currentFile) => {
            broadcastProgress(project.id, {
              progress,
              step,
              currentFile,
            });
          }
        )
        .catch((error) => {
          console.error("Clone error:", error);
        });

      res.json(project);
    } catch (error) {
      res.status(400).json({
        message: error instanceof Error ? error.message : "Failed to create project",
      });
    }
  });

  // Get project by ID
  app.get("/api/projects/:id", async (req, res) => {
    try {
      const project = await storage.getProject(req.params.id);
      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }
      res.json(project);
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Failed to fetch project",
      });
    }
  });

  // Delete project
  app.delete("/api/projects/:id", async (req, res) => {
    try {
      await fileManager.deleteProject(req.params.id);
      await storage.deleteProject(req.params.id);
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Failed to delete project",
      });
    }
  });

  // Get files for a project
  app.get("/api/projects/:id/files", async (req, res) => {
    try {
      const files = await storage.getFilesByProject(req.params.id);
      res.json(files);
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Failed to fetch files",
      });
    }
  });

  // Get file content
  app.get("/api/files/:id", async (req, res) => {
    try {
      const file = await storage.getFile(req.params.id);
      if (!file) {
        return res.status(404).json({ message: "File not found" });
      }
      res.json(file);
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Failed to fetch file",
      });
    }
  });

  // Update file content
  app.patch("/api/files/:id", async (req, res) => {
    try {
      const { content } = req.body;
      await storage.updateFileContent(req.params.id, content);
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Failed to update file",
      });
    }
  });

  // Download project as ZIP
  app.get("/api/projects/:id/download", async (req, res) => {
    try {
      const project = await storage.getProject(req.params.id);
      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }

      const projectDir = path.join("./cloned_sites", req.params.id);

      res.attachment(`${project.name}.zip`);

      const archive = archiver("zip", {
        zlib: { level: 9 },
      });

      archive.on("error", (err) => {
        throw err;
      });

      archive.pipe(res);
      archive.directory(projectDir, false);
      await archive.finalize();
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Failed to download project",
      });
    }
  });

  // Pause project cloning
  app.post("/api/projects/:id/pause", async (req, res) => {
    try {
      const project = await storage.getProject(req.params.id);
      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }

      await storage.updateProjectStatus(req.params.id, "paused", {
        isPaused: 1,
      });

      const updatedProject = await storage.getProject(req.params.id);
      res.json(updatedProject);
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Failed to pause project",
      });
    }
  });

  // Resume project cloning
  app.post("/api/projects/:id/resume", async (req, res) => {
    try {
      const project = await storage.getProject(req.params.id);
      if (!project) {
        return res.status(404).json({ message: "Project not found" });
      }

      await storage.updateProjectStatus(req.params.id, "processing", {
        isPaused: 0,
      });

      // Resume cloning from where it left off
      cloneService
        .cloneWebsite(
          project.id,
          project.url,
          (progress, step, currentFile) => {
            broadcastProgress(project.id, {
              progress,
              step,
              currentFile,
            });
          }
        )
        .catch((error) => {
          console.error("Clone error:", error);
        });

      const updatedProject = await storage.getProject(req.params.id);
      res.json(updatedProject);
    } catch (error) {
      res.status(500).json({
        message: error instanceof Error ? error.message : "Failed to resume project",
      });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { type Project, type InsertProject, type File, type InsertFile } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Projects
  createProject(project: InsertProject): Promise<Project>;
  getProject(id: string): Promise<Project | undefined>;
  getAllProjects(): Promise<Project[]>;
  updateProjectStatus(id: string, status: string, updates?: Partial<Project>): Promise<void>;
  deleteProject(id: string): Promise<void>;

  // Files
  createFile(file: InsertFile): Promise<File>;
  getFile(id: string): Promise<File | undefined>;
  getFilesByProject(projectId: string): Promise<File[]>;
  updateFileContent(id: string, content: string): Promise<void>;
  deleteFilesByProject(projectId: string): Promise<void>;
}

export class MemStorage implements IStorage {
  private projects: Map<string, Project>;
  private files: Map<string, File>;

  constructor() {
    this.projects = new Map();
    this.files = new Map();
  }

  async createProject(insertProject: InsertProject): Promise<Project> {
    const id = randomUUID();
    const project: Project = {
      ...insertProject,
      id,
      status: "pending",
      totalFiles: 0,
      totalSize: 0,
      currentStep: null,
      progressPercentage: 0,
      filesProcessed: 0,
      isPaused: 0,
      createdAt: new Date(),
      completedAt: null,
      errorMessage: null,
    };
    this.projects.set(id, project);
    return project;
  }

  async getProject(id: string): Promise<Project | undefined> {
    return this.projects.get(id);
  }

  async getAllProjects(): Promise<Project[]> {
    return Array.from(this.projects.values()).sort(
      (a, b) => b.createdAt.getTime() - a.createdAt.getTime()
    );
  }

  async updateProjectStatus(
    id: string,
    status: string,
    updates?: Partial<Project>
  ): Promise<void> {
    const project = this.projects.get(id);
    if (project) {
      this.projects.set(id, {
        ...project,
        status,
        ...updates,
        completedAt: status === "complete" ? new Date() : project.completedAt,
      });
    }
  }

  async deleteProject(id: string): Promise<void> {
    this.projects.delete(id);
    await this.deleteFilesByProject(id);
  }

  async createFile(insertFile: InsertFile): Promise<File> {
    const id = randomUUID();
    const file: File = {
      ...insertFile,
      content: insertFile.content ?? null,
      size: insertFile.size ?? null,
      id,
      createdAt: new Date(),
    };
    this.files.set(id, file);
    return file;
  }

  async getFile(id: string): Promise<File | undefined> {
    return this.files.get(id);
  }

  async getFilesByProject(projectId: string): Promise<File[]> {
    return Array.from(this.files.values())
      .filter((file) => file.projectId === projectId)
      .sort((a, b) => a.path.localeCompare(b.path));
  }

  async updateFileContent(id: string, content: string): Promise<void> {
    const file = this.files.get(id);
    if (file) {
      this.files.set(id, { ...file, content });
    }
  }

  async deleteFilesByProject(projectId: string): Promise<void> {
    const filesToDelete = Array.from(this.files.entries())
      .filter(([, file]) => file.projectId === projectId)
      .map(([id]) => id);

    filesToDelete.forEach((id) => this.files.delete(id));
  }
}

export const storage = new MemStorage();

