import { useEffect, useRef, useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { type File as ProjectFile } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { X } from "lucide-react";

interface CodeEditorProps {
  file: ProjectFile | null;
  projectId?: string;
}

declare global {
  interface Window {
    monaco: any;
  }
}

export default function CodeEditor({ file, projectId }: CodeEditorProps) {
  const editorRef = useRef<HTMLDivElement>(null);
  const monacoEditorRef = useRef<any>(null);
  const [monacoLoaded, setMonacoLoaded] = useState(false);
  const [openTabs, setOpenTabs] = useState<ProjectFile[]>([]);
  const { toast } = useToast();

  // Load Monaco Editor
  useEffect(() => {
    if (window.monaco) {
      setMonacoLoaded(true);
      return;
    }

    const script = document.createElement("script");
    script.src = "https://cdnjs.cloudflare.com/ajax/libs/monaco-editor/0.45.0/min/vs/loader.min.js";
    script.async = true;
    script.onload = () => {
      const requirejs = (window as any).require;
      requirejs.config({
        paths: { vs: "https://cdnjs.cloudflare.com/ajax/libs/monaco-editor/0.45.0/min/vs" },
      });
      requirejs(["vs/editor/editor.main"], () => {
        setMonacoLoaded(true);
      });
    };
    script.onerror = () => {
      console.error("Failed to load Monaco Editor");
    };
    document.head.appendChild(script);
  }, []);

  // Initialize editor
  useEffect(() => {
    if (!monacoLoaded || !editorRef.current) return;

    if (!monacoEditorRef.current) {
      monacoEditorRef.current = window.monaco.editor.create(editorRef.current, {
        value: "",
        language: "html",
        theme: "vs-dark",
        automaticLayout: true,
        minimap: { enabled: false },
        fontSize: 14,
        lineNumbers: "on",
        scrollBeyondLastLine: false,
        wordWrap: "on",
      });
    }

    return () => {
      if (monacoEditorRef.current) {
        monacoEditorRef.current.dispose();
        monacoEditorRef.current = null;
      }
    };
  }, [monacoLoaded]);

  // Update editor content when file changes
  useEffect(() => {
    if (!monacoEditorRef.current || !file) return;

    const language =
      file.type === "html"
        ? "html"
        : file.type === "css"
        ? "css"
        : file.type === "js"
        ? "javascript"
        : "plaintext";

    monacoEditorRef.current.setValue(file.content || "");
    window.monaco.editor.setModelLanguage(monacoEditorRef.current.getModel(), language);

    // Add to tabs if not already there
    if (!openTabs.find((t) => t.id === file.id)) {
      setOpenTabs((prev) => [...prev, file]);
    }
  }, [file]);

  const updateFileMutation = useMutation({
    mutationFn: async ({ fileId, content }: { fileId: string; content: string }) => {
      await apiRequest("PATCH", `/api/files/${fileId}`, { content });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "File saved successfully",
      });
      if (projectId) {
        queryClient.invalidateQueries({ queryKey: ["/api/projects", projectId, "files"] });
      }
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSave = () => {
    if (!file || !monacoEditorRef.current) return;
    const content = monacoEditorRef.current.getValue();
    updateFileMutation.mutate({ fileId: file.id, content });
  };

  const closeTab = (tabFile: ProjectFile) => {
    setOpenTabs((prev) => prev.filter((t) => t.id !== tabFile.id));
  };

  const getFileIcon = (type: string) => {
    if (type === "html") return "fab fa-html5 text-orange-400";
    if (type === "css") return "fab fa-css3-alt text-blue-400";
    if (type === "js") return "fab fa-js-square text-yellow-400";
    return "far fa-file-code text-muted-foreground";
  };

  return (
    <main className="flex-1 flex flex-col overflow-hidden">
      {/* Editor Tabs */}
      <div className="bg-muted border-b border-border flex items-center overflow-x-auto">
        {openTabs.map((tab) => (
          <div
            key={tab.id}
            className={`flex items-center gap-2 px-4 py-2 border-r border-border cursor-pointer min-w-max ${
              file?.id === tab.id ? "bg-card border-b-2 border-b-primary" : ""
            }`}
            data-testid={`tab-${tab.path}`}
          >
            <i className={getFileIcon(tab.type)}></i>
            <span className="text-sm">{tab.path.split("/").pop()}</span>
            <button
              className="ml-2 hover:bg-card/50 rounded p-0.5"
              onClick={(e) => {
                e.stopPropagation();
                closeTab(tab);
              }}
            >
              <X className="w-3 h-3 text-muted-foreground" />
            </button>
          </div>
        ))}
      </div>

      {/* Monaco Editor */}
      <div className="flex-1 overflow-hidden">
        {file ? (
          <div ref={editorRef} className="monaco-editor-container" data-testid="code-editor" />
        ) : (
          <div className="flex items-center justify-center h-full text-muted-foreground">
            Select a file to edit
          </div>
        )}
      </div>

      {/* Editor Footer */}
      <div className="bg-muted border-t border-border px-4 py-2 flex items-center justify-between text-xs text-muted-foreground">
        <div className="flex items-center gap-4">
          <span>{file?.type?.toUpperCase() || "TXT"}</span>
          <span>UTF-8</span>
          <span>LF</span>
        </div>
        <div className="flex items-center gap-4">
          <button
            className="px-3 py-1 bg-primary text-primary-foreground rounded hover:bg-primary/90 transition-all"
            onClick={handleSave}
            disabled={!file || updateFileMutation.isPending}
            data-testid="button-save"
          >
            Save (Ctrl+S)
          </button>
        </div>
      </div>
    </main>
  );
}

client/src/components/FileExplorer.tsx.content:
import { useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { type Project, type File as ProjectFile } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { useState } from "react";
import {
  Folder,
  FolderOpen,
  FileCode,
  FileText,
  Image,
  ChevronRight,
  ChevronDown,
  Play,
  Download,
  RefreshCw,
} from "lucide-react";

interface FileTreeNode {
  name: string;
  path: string;
  type: "file" | "folder";
  children?: FileTreeNode[];
  file?: ProjectFile;
}

interface FileExplorerProps {
  files: ProjectFile[];
  currentProject: Project | null;
  onFileSelect: (file: ProjectFile) => void;
  onProjectSelect: (project: Project | null) => void;
}

export default function FileExplorer({
  files,
  currentProject,
  onFileSelect,
}: FileExplorerProps) {
  const [expandedFolders, setExpandedFolders] = useState<Set<string>>(new Set(["root"]));
  const [selectedPath, setSelectedPath] = useState<string>("");
  const { toast } = useToast();

  const downloadMutation = useMutation({
    mutationFn: async (projectId: string) => {
      const response = await fetch(`/api/projects/${projectId}/download`);
      if (!response.ok) throw new Error("Download failed");

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `${currentProject?.name || "project"}.zip`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Project downloaded successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const buildFileTree = (files: ProjectFile[]): FileTreeNode => {
    const root: FileTreeNode = {
      name: currentProject?.name || "Project",
      path: "root",
      type: "folder",
      children: [],
    };

    files.forEach((file) => {
      const parts = file.path.split("/");
      let current = root;

      parts.forEach((part, index) => {
        const isFile = index === parts.length - 1;
        const path = parts.slice(0, index + 1).join("/");

        let child = current.children?.find((c) => c.name === part);

        if (!child) {
          child = {
            name: part,
            path,
            type: isFile ? "file" : "folder",
            children: isFile ? undefined : [],
            file: isFile ? file : undefined,
          };
          current.children?.push(child);
        }

        if (!isFile) {
          current = child;
        }
      });
    });

    return root;
  };

  const toggleFolder = (path: string) => {
    setExpandedFolders((prev) => {
      const next = new Set(prev);
      if (next.has(path)) {
        next.delete(path);
      } else {
        next.add(path);
      }
      return next;
    });
  };

  const handleFileClick = (node: FileTreeNode) => {
    if (node.type === "folder") {
      toggleFolder(node.path);
    } else if (node.file) {
      setSelectedPath(node.path);
      onFileSelect(node.file);
    }
  };

  const getFileIcon = (fileName: string) => {
    const ext = fileName.split(".").pop()?.toLowerCase();
    if (["html", "htm"].includes(ext || "")) return <FileCode className="w-4 h-4 text-orange-400" />;
    if (["css"].includes(ext || "")) return <FileText className="w-4 h-4 text-blue-400" />;
    if (["js", "json"].includes(ext || "")) return <FileText className="w-4 h-4 text-yellow-400" />;
    if (["png", "jpg", "jpeg", "gif", "svg", "webp"].includes(ext || ""))
      return <Image className="w-4 h-4 text-green-400" />;
    return <FileText className="w-4 h-4 text-muted-foreground" />;
  };

  const renderNode = (node: FileTreeNode, depth: number = 0): JSX.Element => {
    const isExpanded = expandedFolders.has(node.path);
    const isSelected = selectedPath === node.path;

    return (
      <div key={node.path}>
        <div
          className={`file-item flex items-center gap-2 px-2 py-1.5 rounded cursor-pointer ${
            isSelected ? "bg-muted" : ""
          }`}
          style={{ paddingLeft: `${depth * 16 + 8}px` }}
          onClick={() => handleFileClick(node)}
          data-testid={`file-item-${node.name}`}
        >
          {node.type === "folder" && (
            <>
              {isExpanded ? (
                <ChevronDown className="w-3 h-3 text-muted-foreground" />
              ) : (
                <ChevronRight className="w-3 h-3 text-muted-foreground" />
              )}
              {isExpanded ? (
                <FolderOpen className="w-4 h-4 text-accent" />
              ) : (
                <Folder className="w-4 h-4 text-accent" />
              )}
            </>
          )}
          {node.type === "file" && getFileIcon(node.name)}
          <span className="text-sm">{node.name}</span>
        </div>
        {node.type === "folder" && isExpanded && node.children && (
          <div>
            {node.children.map((child) => renderNode(child, depth + 1))}
          </div>
        )}
      </div>
    );
  };

  const fileTree = buildFileTree(files);

  return (
    <aside className="w-64 bg-card border-r border-border flex flex-col">
      <div className="p-3 border-b border-border flex items-center justify-between">
        <h2 className="text-sm font-semibold text-foreground">FILES</h2>
        <button
          className="p-1 rounded hover:bg-muted transition-all"
          title="Refresh"
          onClick={() => {
            if (currentProject) {
              queryClient.invalidateQueries({ queryKey: ["/api/projects", currentProject.id, "files"] });
            }
          }}
        >
          <RefreshCw className="w-3 h-3 text-muted-foreground" />
        </button>
      </div>

      <div className="flex-1 overflow-y-auto p-2">
        {currentProject ? (
          renderNode(fileTree)
        ) : (
          <div className="text-sm text-muted-foreground text-center mt-8">
            Clone a website to view files
          </div>
        )}
      </div>

      <div className="p-3 border-t border-border space-y-2">
        <button
          className="w-full bg-accent hover:bg-accent/90 text-accent-foreground px-4 py-2 rounded-lg font-medium transition-all flex items-center justify-center gap-2"
          disabled={!currentProject}
          data-testid="button-run-preview"
        >
          <Play className="w-4 h-4" />
          Run Preview
        </button>
        <button
